	(619,101,'title_ml_text_english','Insufficient exercise, fresh air, natural light'),
	(620,101,'_title_ml_text_english','field_5edfa7bf9ba7d'),
	(621,101,'title_ml_text_chinese',''),
	(622,101,'_title_ml_text_chinese','field_5edfa7989ba7c'),
	(623,101,'title_ml_text_uyghur',''),
	(624,101,'_title_ml_text_uyghur','field_602658467ef5f'),
