	(789,37,'title_ml_text_english','Interrogations, forced confessions, ‘self-criticism’'),
	(790,37,'_title_ml_text_english','field_5edfa7bf9ba7d'),
	(791,37,'title_ml_text_chinese',''),
	(792,37,'_title_ml_text_chinese','field_5edfa7989ba7c'),
	(793,37,'title_ml_text_uyghur',''),
	(794,37,'_title_ml_text_uyghur','field_602658467ef5f'),
