	(1400,133,'_edit_last','1'),
	(1401,133,'_edit_lock','1613385484:1'),
	(1402,133,'title_ml_text_english','To the UN Security Council'),
	(1403,133,'_title_ml_text_english','field_5edfa7bf9ba7d'),
	(1404,133,'title_ml_text_chinese',''),
	(1405,133,'_title_ml_text_chinese','field_5edfa7989ba7c'),
	(1406,133,'title_ml_text_uyghur',''),
	(1407,133,'_title_ml_text_uyghur','field_602658467ef5f'),
	(1408,133,'title_ml_text',''),
	(1409,133,'_title_ml_text','field_5edfa76c9ba7b'),
	(1410,133,'title',''),
	(1411,133,'_title','field_602657cc293e7'),
	(1412,133,'content_ml_rich_text_english','<ul>\r\n 	<li>Hold regular, formal public meetings on the situation in Xinjiang, China in order to allow relevant UN entities, as well as members of civil society and human rights defenders to brief members directly on the latest situation on the ground.</li>\r\n 	<li>Adopt a resolution that sends an unambiguous message to the Chinese authorities, condemning the situation and demanding the dismantling of the internment camps system as well as all laws and regulations, and end all related measures, that impermissibly restrict the rights of Uyghurs and other predominantly Muslim ethnic groups. In addition, the resolution should demand immediate and unfettered access for independent investigators.</li>\r\n</ul>'),
	(1413,133,'_content_ml_rich_text_english','field_5edfa91e024b0'),
	(1414,133,'content_ml_rich_text_chinese',''),
	(1415,133,'_content_ml_rich_text_chinese','field_5edfa91e0243c'),
	(1416,133,'content_ml_rich_text_uyghur',''),
