('1074', '118', '_edit_lock', '1613384716:1'),
('1075', '118', 'title_ml_text_english', 'Survivor accounts of the release process'),
('1076', '118', '_title_ml_text_english', 'field_5edfa7bf9ba7d'),
('1077', '118', 'title_ml_text_chinese', ''),
('1078', '118', '_title_ml_text_chinese', 'field_5edfa7989ba7c'),
('1079', '118', 'title_ml_text_uyghur', ''),
